import React,  { Component } from 'react';
import { connect } from 'react-redux';
import { Tabs, Tab, AppBar } from '@material-ui/core';
import AppBarMD from '@material-ui/core/AppBar';
import { TabPanel } from '@material-ui/lab';
import { useState } from "react";

let newIndex = 0
let lastIndex
const routes = [
  '/',
  '/articles'
]

class TextInfo extends Component {
  handleChange = (event, index) => {
    lastIndex = newIndex
    newIndex = index
    Router.push(routes[index])
  };
  constructor(props) {
    super(props);
  }
  componentDidMount () {
    if (typeof lastIndex !== 'undefined') {
      setTimeout(() => {
        lastIndex = undefined
        this.forceUpdate()
      }, 0)
    }
  }


  render(){
    const index = typeof lastIndex === 'undefined'
      ? newIndex
      : lastIndex
    return(
    <div>
      <AppBarMD position='static'>
        <Tabs index={index} onChange={this.handleChange}>
          <Tab label="Item One"  />
          <Tab label="Item Two"  />
          <Tab label="Item Three"  />
        </Tabs>
      </AppBarMD>
    </div>
    );
  }
}

TextInfo = connect((state) => state)(TextInfo);
export default TextInfo;
