import { loadGetInitialProps } from 'next/dist/next-server/lib/utils';
import { createStore, applyMiddleware } from 'redux';
import thunkMiddleware from 'redux-thunk';

const initial = {
  message:'START',
  count: 0,
  // 単語ステート
  data:[{English:'apple', Japanese:'りんご'},
        {English:'banana', Japanese:'バナナ'},
        {English:'chocolate', Japanese:'チョコレート'},
        {English:'red', Japanese:'赤'},
        {English:'white', Japanese:'白'} ],
  redult:0,
  status: "default",
  questioncount: 0,
}

function counterReducer (state = initial, action) {
  switch (action.type) {
    case 'INCREMENT':
      return {
        message:'INCREMENT',
        count:state.count + 1
      };
    case 'DECREMENT':
      return {
        message:'DECREMENT',
        count: state.count -1
      };
    case 'RESET':
      return {
        message: 'RESET',
        count:initial.count
      };
    // 単語reducer
    case 'START':
      return startReduce(state, action);
    case 'CHECK':
      return checkReduce(state, action);

    default:
      return state;
  }
}

function startReduce(state, action) {
  let q = state.questioncount + 1;
  return {
    message:'START',
    count: 0,
    data:state.dtata,
  }
}

export function initStore(state = initial) {
  return createStore(counterReducer, state, applyMiddleware(thunkMiddleware))
}
